mapper.delete:
DELETE FROM FEE_YLSFXMDJ WHERE
JGID = #{jgid}  AND 
FYXH = #{fyxh}

mapper.select:
SELECT
FYXH AS YLXH,
FYDJ AS YLDJ
FROM
FEE_YLSFXMDJ


		AND ZDCR = #{zdcr}


		AND ZFPB = #{zfpb}


		AND JGID = #{jgid}

select a.JGID as JGID, a.FYXH as FYXH, b.FYMC as FYMC, b.FYGB as FYGB, b.FYDW as FYDW, b.PYDM as PYDM, a.FYDJ as FYDJ, b.MZSY as MZSY, b.ZYSY as ZYSY, b.YJSY as YJSY, b.TJFY as TJFY, b.TXZL as TXZL, b.WBDM as WBDM, b.JXDM as JXDM, b.BHDM as BHDM, b.XMLX as XMLX, b.QTDM as QTDM, a.FYKS as FYKS, a.ZDCR as ZDCR, a.XZSY as XZSY, a.XZTS as XZTS, a.XZCS as XZCS, a.SBJG as SBJG, a.CPTCODE as CPTCODE, a.YWMC as YWMC, a.DZBL as DZBL, b.BZJG as BZJG, a.JGBZ as JGBZ, b.XMBM as XMBM, b.HMBM as HMBM, a.ZFPB as ZFPB, a.JFYZ as JFYZ from FEE_YLSFXMDJ a, FEE_YLSFXM b where b.FYXH = a.FYXH and a.JGID = #{jgid}
and b.FYGB = #{fygb}
and b.PYDM like concat(concat('%',UPPER(#{pydm})),'%')

SELECT sf.FYXH , sf.FYMC , sf.FYDW , sf.BZJG , sf.XMLX , sf.FYGB , mx.FYDJ , mx.SBJG , mx.FYKS FROM FEE_YLSFXM sf, FEE_YLSFXMDJ mx WHERE sf.FYXH = mx.FYXH AND mx.ZFPB = 0 AND sf.ZFPB = 0 AND mx.JGID = #{jgid}
AND sf.XMLX = #{xmlx}
AND sf.YJSY = 1
and sf.FYGB = 9 and sf.YJSY =1
AND EXISTS (SELECT BMXH FROM PUB_FYBM bm WHERE bm.FYXH = sf.FYXH AND bm.PYDM LIKE concat(concat('%',#{pydm}),'%') )
